import axios from 'axios';

export const fetchPizza = () => (dispatch) => {
    axios.get('http://localhost:3001/pizzas').then(({ data }) => {
        dispatch(setPizza(data))
    })
}

export const setPizza = (items) => ({
    type: 'SET_PIZZA',
    payload: items,
})
