const initialState = {
    items: {},
    totalPrice: 0,
    totalCount: 0
}

const cart = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD_PRODUCT_CART':

            const itemsData = {
                ...state,
                items: {
                    [action.payload.id]: !state.items[action.payload.id] ? [action.payload] :
                        [...state.items[action.payload.id], action.payload],
                }
            }

            const productArr = [].concat.apply([], Object.values(items));

            return {
                ...state,
                items: itemsData,
                totalCount: productArr.length,
                totalPrice: productArr.reduce()
            }

    }
    return state;
}

export default cart;
