const initialState = {
    items: {},
    totalPrice: 1,
    totalCount: 1
}

const cart = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_TOTAL_PRICE':
            return {
                ...state,
                totalPrice: action.payload
            }
        case 'SET_TOTAL_COUNT':
            return {
                ...state,
                totalCount: action.payload
            }
    }
    return state;
}

export default cart;
