const initialState = {
    category: null,
    sortBy: 'popular'
}

const filter = (state = initialState, action) => {
    if (action.type === 'SET_SORT') {
        return {
            ...state,
            sortBy: action.payload
        }
    }

    if (action.type === 'SET_CATEGORY') {
        return {
            ...state,
            category: action.payload
        }
    }

    return state;
}

export default filter;
