import React from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
// import { useDispatch, useSelector } from 'react-redux';
// import { addPizzaToCart } from '../redux/actions/cart';

function ProductBlock({ name, imageUrl, price, types, sizes, onClickAddProduct }) {

    const availableType = ['тонкое', 'традиционное'];
    const availableSize = [26, 30, 40];
    const [activeType, setActiveType] = React.useState(types[0]);
    const [activeSize, setActiveSize] = React.useState(0);


    function handleType(index) {
        setActiveType(index);
    }

    function handleSize(index) {
        setActiveSize(index);
    }

    function addProduct() {

        const productObj = {
            name,
            imageUrl,
            price,
            type: activeType,
            size: activeSize
        }
        onClickAddProduct(productObj);
    }




    return (
        <div className="pizza-block">
            <img
                className="pizza-block__image"
                src={imageUrl}
                alt="{name}"
            />
            <h4 className="pizza-block__title">{name}</h4>
            <div className="pizza-block__selector">
                <ul>
                    {availableType.map((type, index) => (
                        <li onClick={() => handleType(index)}
                            className={classNames({
                                active: activeType === index,
                                disabled: !types.includes(type)
                            })}
                            key={type}>{type}</li>
                    ))}

                </ul>
                <ul>
                    {availableSize.map((size, index) => (
                        <li onClick={() => handleSize(index)}
                            className={classNames({
                                active: activeSize === index,
                                disabled: !sizes.includes(size)
                            })}
                            key={size}>{size}</li>
                    ))}

                </ul>
            </div>
            <div className="pizza-block__bottom">
                <div className="pizza-block__price">от {price} ₽</div>
                <div onClick={addProduct} className="button button--outline button--add">
                    <svg
                        width="12"
                        height="12"
                        viewBox="0 0 12 12"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                    >
                        <path
                            d="M10.8 4.8H7.2V1.2C7.2 0.5373 6.6627 0 6 0C5.3373 0 4.8 0.5373 4.8 1.2V4.8H1.2C0.5373 4.8 0 5.3373 0 6C0 6.6627 0.5373 7.2 1.2 7.2H4.8V10.8C4.8 11.4627 5.3373 12 6 12C6.6627 12 7.2 11.4627 7.2 10.8V7.2H10.8C11.4627 7.2 12 6.6627 12 6C12 5.3373 11.4627 4.8 10.8 4.8Z"
                            fill="white"
                        />
                    </svg>
                    <span>Add to cart</span>
                    <i>2</i>
                </div>
            </div>
        </div>
    )
}

export default ProductBlock;
