import axios from 'axios';

export const fetchProduct = () => (dispatch) => {
    axios.get('http://localhost:3001/pizzas').then(({ data }) => {
        dispatch(setProduct(data))
    })
}

export const setProduct = (items) => ({
    type: 'SET_PRODUCT',
    payload: items,
})
