import axios from 'axios';

export const fetchProduct = (sortBy, category) => (dispatch) => {
    const checkCategory = category !== null ? category : '';
    axios.get(`http://localhost:3001/pizzas/category=${checkCategory}`).then(({ data }) => {
        dispatch(setProduct(data))
    })
}

export const setProduct = (items) => ({
    type: 'SET_PRODUCT',
    payload: items,
})
