const initialState = {
    items: {},
    totalPrice: 0,
    totalCount: 0
}

const cart = (state = initialState, action) => {
    if (action.type === 'SET_TOTAL_PRICE') {
        return {
            ...state,
            items: action.payload
        }
    }
    return state;
}

export default cart;
