import React from 'react';
import { Categories, Sort, Pizza } from '../components';
import { useDispatch, useSelector } from 'react-redux';
import { setPizza } from '../redux/actions/pizza';
import axios from 'axios';

function Home() {

    const dispatch = useDispatch();
    const items = useSelector(({ pizza }) => pizza.items);

    React.useEffect(() => {
        axios.get('http://localhost:3001/pizzas').then(({ data }) => {
            dispatch(setPizza(data))
        });
    }, []);

    return (
        <div className="container">
            <div className="content__top">
                <Categories items={['Первое', 'Второе']} />
                <Sort items={['популярности', 'цене', 'алфавиту']} />
            </div>
            <h2 className="content__title">Все пиццы</h2>
            <div className="content__items">
                {items.map((obj) => (
                    <Pizza key={obj.id} {...obj} />
                ))}

            </div>
        </div>
    )

}


export default Home;
